INSERT INTO `cm_article` VALUES ('4028380439cf997c0139d2cff85510f9', 'phone01', 'iPhone', 'The first generation iPhone was released on June 29, 2007.', '<p></p><p><font face=\"Times New Roman\" size=\"4\"><b>1. About iPhone</b></font></p><p><font size=\"4\"><font face=\"Times New Roman\">&nbsp; &nbsp; Development of what was to become the iPhone began <font color=\"#333333\">in <b>2004</b>, when Apple started to gather a team of <b>1000</b> employees to work on the highly confidential \"<b>Project Purple</b>\",</font></font><font color=\"#333333\"><span style=\"font-family: \'Times New Roman\';\">including </span><a href=\"http://en.wikipedia.org/wiki/Jonathan_Ive\" style=\"font-family: \'Times New Roman\';\"><b>Sir Jonathan Ive</b></a><span style=\"font-family: \'Times New Roman\';\">, the designer behind the iPhone. Apple created the device during a secretive collaboration with </span><b style=\"font-family: \'Times New Roman\';\">AT&amp;T</b><span style=\"font-family: \'Times New Roman\';\"> Mobility -&nbsp;</span><font style=\"font-family: \'Times New Roman\';\"><b>Cingular</b></font><span style=\"font-family: \'Times New Roman\';\"> Wireless at the time - at an estimated development cost of <b>US</b></span><font style=\"font-family: \'Times New Roman\';\"><b>$150</b> million</font><span style=\"font-family: \'Times New Roman\';\"> over thirty months.</span></font></font></p><p><font face=\"Times New Roman\" size=\"4\"><font color=\"#333333\">&nbsp; &nbsp; Apple rejected the \"design by committee\" approach that had yielded the Motorola ROKR E1, a largely unsuccessful collaboration with Motorola. Instead, Cingular gave Apple the liberty to develop the iPhone\'s hardware and sof</font>tware in-house and even paid Apple a fraction of its monthly service revenue (until the iPhone 3G), in exchange for four years of exclusive US sales, until 2011.</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><div><p><b><font size=\"4\">2. Resource</font></b></p><p><b><font size=\"4\"><br></font></b></p><p><a href=\"http://www.youtube.com/watch?feature=player_embedded&amp;v=LzfAnnvOg3I\"><font size=\"4\">iPhone 5 Review</font></a></p><p><b><font size=\"4\"><br></font></b></p><p><font size=\"4\"><a href=\"http://www.youtube.com/watch?feature=player_embedded&amp;v=dlfCVkszKWE\">iPhone 4 Review (Verizon) </a><br></font></p><p><font size=\"4\"><br></font></p><p><font size=\"4\"><a href=\"http://www.youtube.com/watch?feature=player_embedded&amp;v=znKE2c7hCis\">iPhone 3GS Review</a><br></font></p><p><font size=\"4\"><br></font></p><p><a href=\"http://www.youtube.com/watch?feature=player_embedded&amp;v=HwFExNaicwM\"><font size=\"4\">iPhone 3G Review</font></a></p><p><br></p><p><a href=\"http://www.m1.com.sg/iPhone/purchase.html\">M1</a> :&nbsp;<strong style=\"color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif; font-size: 12px; background-color: rgb(255, 255, 255);\">Paragon &nbsp;</strong><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif; font-size: 12px;\">290 Orchard Road &nbsp;</span><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif; font-size: 12px;\">#B1-28/29 Paragon&nbsp;</span><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif; font-size: 12px;\">Singapore 238859, &nbsp;</span><font color=\"#000000\" face=\"trebuchet ms, Tahoma, sans-serif\"><span style=\"font-size: 12px;\">Bugis Junction&nbsp;</span></font><span style=\"font-size: 12px; color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif;\">200 Victoria Street&nbsp;</span><span style=\"font-size: 12px; color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif;\">#01-81/82 Bugis Junction&nbsp;</span><span style=\"font-size: 12px; color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif;\">Singapore 188021&nbsp;</span></p><p><span style=\"font-size: 12px; color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif;\"><br></span></p><p><span style=\"font-size: 12px; font-family: \'trebuchet ms\', Tahoma, sans-serif; color: rgb(0, 0, 0);\"><a href=\"http://www.starhub.com/content/support/mobile/mobileservicestatistics/handset-stocks-availability.html\">Starhub</a> :&nbsp;<span id=\"sceditor-end-marker\" class=\"sceditor-selection sceditor-ignore\" style=\"line-height: 0; display: none;\"></span><span id=\"sceditor-start-marker\" class=\"sceditor-selection sceditor-ignore\" style=\"line-height: 0; display: none;\"></span></span></p><p><span style=\"font-size: 12px; color: rgb(0, 0, 0); font-family: \'trebuchet ms\', Tahoma, sans-serif;\"><br></span></p><p><font color=\"#000000\" face=\"trebuchet ms, Tahoma, sans-serif\"><span style=\"font-size: 12px;\"><br></span></font></p><p><br></p><p></p></div>', 'Gan Jianping', 'Apple iPhone 5c.jpeg', 'http://www.apple.com/iphone', 'Hardware,Phone,Apple', '100', 'en_SG', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2012-09-17 14:00:08', '2013-09-27 14:24:11', '0');
INSERT INTO `cm_article` VALUES ('4028380439d80ea20139d8ab79170714', 'english01', '求职日常英语基本口语用语及服务行业日常英语', 'uniform 制服；工作服', '<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">服务行业基础英语</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">A. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;WORDS &amp; EXPRESSIONS (常用词语)</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;PERSONAL（个人物品）</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">1. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; uniform &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 制服；工作服</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">2. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; shoes &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;鞋子</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">3. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cosmetics &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;化妆品</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">4. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; I/C no. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;身份证号码</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">5. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; passport &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 护照</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">6. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; experience &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;经验；经历</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; COMPANY &nbsp;( &nbsp; &nbsp; &nbsp;公司 )</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">1. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; employer &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;雇主；公司</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">2. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; employee &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 雇员；员工</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">3. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; normal working time &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 正常工作时间</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">4. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; shift &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;轮班</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">5. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; over time=o/t &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 加班</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">6. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; supervisor &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;主管/上司</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">7. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; manager &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 经理</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">8. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; HR（human reasourece) &nbsp; &nbsp; &nbsp;人事部；人力资源部</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">9. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; department （dept） &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 部门</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">10. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;supervisor &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 主管/上司</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">11. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;manager &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;经理</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">12. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;training &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;培训</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">13. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;communication &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;沟通</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">14. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;probation period &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 试用期</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">15. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;responsibility / duty &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 责任</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">16. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;bank account &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 银行帐户</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">17. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sick / ill &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 生病</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">18. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sick leave &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 病假</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">19. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;annual leave &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;年假</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">20. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;work permit &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 工作准证</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">21. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;hostel &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 宿舍</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">22. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;rules and regulations &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;规章制度</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">23. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;on time / punctual &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 准</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WORK ENVIRONMENT &nbsp;( 工作环境 )</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">1. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; house keeping &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 打扫卫生</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">2. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; queue &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 排队；等候</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">3. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; customer &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 客户</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">4. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; service &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;服务</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">5. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; menu &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;菜单</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">6. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; safety &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 安全</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">7. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; entrance &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;入口</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">8. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; exit &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;出口</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">9. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; careful &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 小心的；细心的；认真的</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">10. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;no smoking &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;禁止吸烟 &nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">11. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;push &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;推 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">12. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;pull &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 拉</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">13. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;business hours &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 营业时间 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">14. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;open &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(营业场所) 营业 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">15. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;closed &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(营业场所) 不营业;关门</span></p>', 'quote', '', 'http://65start.com/article-34-1.html', '学习,英文,工作', '100', 'zh_CN', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2012-09-18 17:17:59', '2013-05-06 00:15:16', '0');
INSERT INTO `cm_article` VALUES ('4028380439d80ea20139d8bfa67a0a3d', '', 'Methods for handling complaints and dealing with angry guests', '1.Listen; 2.Acknowledge; 3.Restate; 4.Ask; 5.Action; 6:Follow-up;', '<p>&nbsp;</p>\r\n<p><strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">1. &nbsp;Listen</span></strong></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Listen critically, without interruption. &nbsp;Do not argue or deny anything. &nbsp;Do not get defensive or challenged in any way even if the guest is abusive, exaggerates, or speaks boisterously. Be attentive.</span></p>\r\n<p><strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">2. &nbsp;Acknowledge</span></strong></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Express empathy towards the guest and the existence of a problem. &nbsp;Do not accept nor pass on the blame to others until all the facts are gathered. &nbsp;Use the &ldquo;sad/glad&rdquo; statement.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Sad &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- I am sorry there is a problem.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Glad &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- I am glad you are bringing it to our attention.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Empathise &nbsp; - I am certain that this has been frustrating for you. &nbsp;I do understand your &nbsp;being so upset.</span></p>\r\n<p><strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">3. &nbsp;Restate</span></strong></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">As soon as the guest has calmed down, restate the complaint in question form, in order to</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">avoid misunderstandings and to show the guest that you possess good comprehension skills.</span></p>\r\n<p><strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">4. &nbsp;Ask</span></strong></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Ask the guest what he/she wants you to do or what he/she would like to be done. &nbsp;In many cases, the guest is merely satisfied with just &ldquo;letting the company know&rdquo; what has occurred. &nbsp;If he/she wants something done specifically, find out exactly what it is and confirm it with him/her. Stress what you can do, not what you can&rsquo;t do. &nbsp;Suggest alternatives if preferred solution is not feasible. &nbsp;If the alternative solution is not satisfactory to them, seek the assistance of a person of higher authority than yourself.</span></p>\r\n<p><strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">5. &nbsp;Action</span></strong></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Find something to agree on. &nbsp;Once a solution has been agreed upon, take immediate action and do it! &nbsp;Notify the guest of any changes or delays. &nbsp;Never promise anything that you cannot keep.</span></p>\r\n<p><strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">6. &nbsp;Follow-up</span></strong></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">After a problem has been remedied, contact the guest and make sure that the solution was</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">satisfactory to them. &nbsp;Use this moment of truth to thank the guest for brining the matter to your awareness and accentua</span></p>\r\n<p>&nbsp;</p>', 'quote', '/resources/upload/article/complain.png', 'http://www.businessknowhow.com/manage/crmindset.htm', 'Learning,Work Experience', '100', 'en_SG', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2012-09-18 17:40:02', '2013-05-06 00:15:19', '0');
INSERT INTO `cm_article` VALUES ('4028380439d80ea20139d8c234690a6e', 'english03', '工作能力中英文表达', 'Mature，dynamic and honest．思想成熟、有活力、为人诚实。', '<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Mature，dynamic and honest．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">思想成熟、有活力、为人诚实。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Excellent ability of systematical management．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有极强的系统管理能力。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Ability to work independently．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">能够独立工作。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">A person with ability plus flexibility should apply.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">需要有能力及适应力强的人。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">A stable personality and high sense of responsibility are desirable.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">需要个性稳重、具高度责任感的人。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Work well with a multicultural work force.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">能够和不同文化的人一起出色地工作。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Bright，aggressive applicants.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">开朗、有进取心的应聘者。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Ambitious attitude essential．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">要有雄心壮志。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Willing to work under pressure with leadership quality．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">愿意在压力下工作，并具领导素质。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Willing to assume responsibilities.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">应聘者须勇于挑重担。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Mature，self-motivated and strong interpersonal skills.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">思想成熟、上进心强，并具有丰富的人际关系技巧。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Energetic，fashion-minded person.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">精力旺盛、思想新潮的人。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Strong determination to succeed．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有获取成功的坚定决心。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Strong leadership skills．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有极强的领导才能。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Ability to work well with others.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">能够和他人一道很好地工作。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Highly-motivated and reliable person with excellent health and pleasant personality.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">需要上进心强、可靠、身体健康和性格开朗的人。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">The ability to take initiative and operate independently.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">积极主动、并能独立工作的人。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Strong leadership skill while possessing a great team spirit.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有很强的领导才能并具很强的集体精神。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Be highly organized and efficient．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">工作很有条理，办事效率高。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Willing to learn and progress．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">肯学习进取。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Good presentation skills.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有良好的表达能力。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Positive active mind essential．</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">思想不消极、头脑灵活。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Ability to deal with personnel at all levels effectively。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">善于同各种人员打交道。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Have positive work attitude and be willing and able to work diligently without supervision。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有积极的工作态度，愿意并能够在没有监督的情况下勤奋地工作。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Young，bright，energetic with strong career ambition.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">年轻、聪明、精力充沛，并有很强的事业心。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Good people management and communication skills. Team player.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有良好的人员管理和交际能力。有集体合作精神。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Be able to work under high pressure and time limitation.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">能够在高压力下并有时间限制的条件下工作。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Be elegant and with nice personality.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">举止优雅、个人性格好。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">With good managerial skills and organizational capabilities.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有良好的管理艺术和组织能力。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">The main qualities required are preparedness to work hard, ability to learn, ambition and good health.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">主要必备的素质是能吃苦耐劳、学习能力强、有事业心并且身体健康。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Having good and extensive social connections.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">具有良好而广泛的社会关系。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Being active, creative and innovative is a plus.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">思想活跃、有首创和革新精神尤佳。</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">With good analytical capability.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">有较强的分析能力。</span></p>', 'quote', '', 'http://65start.com/article-31-1.html', '学习,英文,工作', '100', 'zh_CN', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2012-09-18 17:42:49', '2013-05-06 00:15:22', '0');
INSERT INTO `cm_article` VALUES ('4028380439d80ea20139d8c4c1310a77', 'english04', '中英文常用职位', 'Accounting Assistant 会计助理', '<table class=\"vwtb\" style=\"word-wrap: break-word; empty-cells: show; border-collapse: collapse; table-layout: fixed; width: 698px; color: #000000; font-family: Tahoma, \'Microsoft Yahei\', Simsun; font-size: 14px; line-height: 25px;\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody style=\"word-wrap: break-word;\">\r\n<tr style=\"word-wrap: break-word;\">\r\n<td id=\"article_content\" style=\"word-wrap: break-word;\">\r\n<div style=\"word-wrap: break-word;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Accounting Assistant&nbsp;&nbsp;&nbsp;&nbsp; 会计助理&nbsp;</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Accounting Clerk&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 记帐员&nbsp;</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Accounting Manager&nbsp;&nbsp;&nbsp;&nbsp; 会计部经理</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Accounting Stall&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 会计部职员&nbsp;</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Accounting Supervisor&nbsp;&nbsp;&nbsp;&nbsp; 会计主管&nbsp;</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Acting President&nbsp;&nbsp;&nbsp;&nbsp; 代理总裁&nbsp;</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Actor&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 演员&nbsp;</span><br style=\"word-wrap: break-word;\" /><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Actress&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 女演员</span><br style=\"word-wrap: break-word;\" /><span style=\"f