INSERT INTO `cm_article` VALUES ('8a3321b63ef3031f013efaf3880e0044', '', 'Key Phrases', 'make/cancel a reservation', '<p></p><p></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">May I have your name?</span><br></p><div><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Could you spell that?</span><br></p></div><div><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">How will you be paying?</span><br></p></div><div><p><font size=\"3\" face=\"Times New Roman\">What\'s the expiration/expiry date?</font></p></div><div><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Will there be anything else?</span><font size=\"3\" face=\"Times New Roman\"><br></font></div><div><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Do you take credit cards?<span id=\"sceditor-end-marker\" style=\"line-height: 0; display: none;\" class=\"sceditor-selection sceditor-ignore\"></span><span id=\"sceditor-start-marker\" style=\"line-height: 0; display: none;\" class=\"sceditor-selection sceditor-ignore\"></span></span><br></p></div><div><p><font face=\"Times New Roman\" size=\"3\">Would like to cancel this reservation?</font></p></div><div><font face=\"Times New Roman\" size=\"3\"><br></font></div><div><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">When would you like to check in/out? (Hotel)</span><br></div><p></p><p></p><p><font size=\"3\" face=\"Times New Roman\">How many nights? (Hotel)</font></p><div><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Would you like a smoking room or non-smoking room?</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">(Hotel)</span><font size=\"3\" face=\"Times New Roman\"><br></font></div><div><p><font size=\"3\" face=\"Times New Roman\">How much are the rooms?/</font><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">What are your rates?</span></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Do you have a reservation/confirmation number?</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\"><br></span></p></div><div><p></p><p></p><p><br></p><p></p><p></p></div><div><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">I\'d like to make a reservation.</span><br></div><p></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Let me confirm the number.</span></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">I need to change my reservation.</span><br></p><p><font face=\"Times New Roman\" size=\"3\">Let me bring you up on the system.</font></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Nobody give me a reservation number.</span><br></p><p><font face=\"Times New Roman\" size=\"3\">I don\'t think I ever got one.&nbsp;</font><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">I never got one. I didn\'t get one.</span></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">I only want to/just need to change the date.</span><br></p><p><font face=\"Times New Roman\" size=\"3\">It seems that all of our rooms are booked.</font></p><p><font face=\"Times New Roman\" size=\"3\">How much is the Deluxe Executive again?</font></p><p><font face=\"Times New Roman\" size=\"3\">I can\'t stand the smell of smoke.</font></p><p><font face=\"Times New Roman\" size=\"3\">I\'m going to have to pass on that.</font></p><p>Ok, that does it.</p><p><br></p><p></p>', 'Gan Jianping', '', '', '', '100', 'en_SG', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2013-05-31 14:17:44', '2013-09-21 22:58:23', '0');
INSERT INTO `cm_article` VALUES ('8a3321b63ef3031f013f009d3a7d00d1', '', 'Mobile Web Development JavaScript Frameworks', 'jQuery Mobile, Titanium Mobile, The-M-Project, Jo, xui.js, EmbedJS, zepto.js, DHTMLX Touch, Mobilize.js, ChocolateChip Mobile', '<p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">1.&nbsp;<a href=\"http://jquerymobile.com/\" target=\"_blank\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">jQuery Mobile</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://jquerymobile.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-02_jquery_mobile.jpg\" width=\"550\" height=\"265\" alt=\"jQuery&nbsp;Mobile\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">jQuery Mobile, a framework that will help you rapidly and confidently develop mobile app user interfaces across popular mobile device platforms like iOS and Android, is perhaps the most popular project out there.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">The framework boasts a lightweight code base (weighing in at 20KB when minified and gzipped) and a huge bevy of standard user interface elements that mobile device users are accustomed to, such as switches and sliders.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">See jQuery Mobile in action by going to its&nbsp;<a href=\"http://jquerymobile.com/demos/1.0b2/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">demo page</a>&nbsp;(it doubles as the framework’s official documentation).</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">If&nbsp;<a href=\"http://sixrevisions.com/javascript/getting-started-with-jquery/\" title=\"Getting Started with jQuery\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">jQuery</a>&nbsp;isn’t your thing, check out&nbsp;<a href=\"http://cpojer.net/MooTools/mobile/Demos/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">MooTools Mobile</a>, a MooTools plugin by MooTools core developer Christoph Pojer and&nbsp;<a href=\"http://dojotoolkit.org/features/mobile\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Dojo Mobile</a>, which leverages the Dojo API to help developers rapidly build apps for mobile platforms.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">2.&nbsp;<a href=\"http://www.appcelerator.com/products/titanium-mobile-application-development/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Titanium Mobile</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://www.appcelerator.com/products/titanium-mobile-application-development/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-03_titanium_mobile.jpg\" width=\"550\" height=\"265\" alt=\"Titanium Mobile\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">This powerful and robust mobile web development framework allows you to use your existing HTML, CSS and JavaScript knowledge to make native-like mobile apps for iOS and Android.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">As one of the bigger mobile web development frameworks out there — with over 300 APIs and an active developer community — you know you’ll often find help and support from peer developers.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">Titanium Mobile supports native UI elements for iOS and Android like table views, tabs, switches and popovers. It also has features that enable you to interact with the mobile devices’ camera and native file storage system.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">3.&nbsp;<a href=\"http://the-m-project.net/\" target=\"_blank\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">The-M-Project</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://the-m-project.net/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-04_the_m_project.jpg\" width=\"550\" height=\"265\" alt=\"The-M-Project\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">The-M-Project is another solid JavaScript framework that takes advantage of new HTML5 features for easier and better mobile app development.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">The framework follows the popular model-view-controller (MVC) software architecture pattern, which is a huge selling point for many developers.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">It has offline support so that your users can continue working without an Internet connection (they can then sync up to the server next time they’re online) and excellent documentation (the project has a&nbsp;<a href=\"http://the-m-project.net/getStarted.html\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Getting Started</a>&nbsp;guide for new users of the project).</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">To witness The-M-Project in action, check out&nbsp;<a href=\"http://the-m-project.net/showcase.html\" title=\"The-M-Project showcase\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">their showcase</a>, which includes items like the&nbsp;<a href=\"http://the-m-project.net/apps/todos2/index.html\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">ToDo App</a>&nbsp;and&nbsp;<a href=\"http://the-m-project.net/apps/kitchensink/index.html\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">KitchenSink</a>&nbsp;(a showcase of all the UI elements included in the framework).</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">4.&nbsp;<a href=\"http://joapp.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Jo</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://joapp.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-05_jo.png\" width=\"550\" height=\"265\" alt=\"Jo\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">Jo is a framework aimed at developers working on HTML5-capable mobile devices such as those the use the iOS, webOS, Android and Chrome OS platforms.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">It has standard, native-like UI elements such as the web form widget for your login screens and the popup widget that you can use for providing extra information when a user clicks on an interface element.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">See the&nbsp;<a href=\"http://joapp.com/demos.html\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">demo page</a>&nbsp;on their site, which includes a screencast displaying Jo in action on multiple devices and mobile platforms.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">5.&nbsp;<a href=\"http://xuijs.com/\" target=\"_blank\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">xui.js</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://xuijs.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-06_xui_js.png\" width=\"550\" height=\"265\" alt=\"xui.js\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">If you need something super lightweight and you only need features for standard client-side development tasks such as DOM manipulation, event-handling,&nbsp;<a href=\"http://sixrevisions.com/category/ajax/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Ajax</a>&nbsp;and a few animation effects, check out xui.js. xui.js packs a punch in such a tiny package (4.2 KB when gzipped).</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">xui.js provides targeted builds for specific mobile browsers such as WebKit and IE mobile for when you’re developing apps strictly towards a particular mobile device operating system.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">6.&nbsp;<a href=\"http://uxebu.github.com/embedjs/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">EmbedJS</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://uxebu.github.com/embedjs/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-07_embedjs.jpg\" width=\"550\" height=\"265\" alt=\"EmbedJS\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">EmbedJS is a JavaScript framework for embedded devices (which includes mobile phones, TVs, tablets and so forth).</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">What’s great about EmbedJS is that it has multiple builds for specific platforms and browsers such as iOS, Firefox, Android, etc., which translates to an optimal user experience and less code to serve to your users. And if you’d like to customize your build, the creators provide you with the&nbsp;<a href=\"https://github.com/uxebu/embedjs-tools#readme\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">EmbedJS Build tool</a>.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">EmbedJS is based on Dojo, so if you’re familiar with the Dojo API syntax, you’ll be able to use EmbedJS right away.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">7.&nbsp;<a href=\"http://zeptojs.com/\" target=\"_blank\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">zepto.js</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://zeptojs.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-08_zepto_js.jpg\" width=\"550\" height=\"265\" alt=\"zepto.js\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">zepto.js is a mobile JavaScript framework built specifically for mobile WebKit browsers (e.g. Safari and Chrome). It prides itself in its minimalist development philosophy, which translates to simplicity for helping developers get stuff done quickly. What’s more is that this JS framework is ultra-lightweight at only 5KB.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">The zepto.js syntax borrows from, and is compatible with, jQuery — a welcome plus for many developers.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">To learn more about this framework, see developer Thomas Fuchs’&nbsp;<a href=\"http://mir.aculo.us/2010/10/28/zepto-js-a-jquery-compatible-mobile-javascript-framework-in-2k-presentation/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">presentation of zepto.js</a>.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">8.&nbsp;<a href=\"http://dhtmlx.com/touch/\" target=\"_blank\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">DHTMLX Touch</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://dhtmlx.com/touch/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-09_dhtmlx_touch.jpg\" width=\"550\" height=\"265\" alt=\"DHTMLX Touch\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">DHTMLX Touch, a free HTML5/JavaScript framework optimized for touchscreen devices, brings you the ability to craft beautiful mobile web apps quickly.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">The&nbsp;<a href=\"http://dhtmlx.com/touch/designer.shtml\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">DHTMLX Touch UI Designer</a>&nbsp;is a visual editor for building mobile user interfaces, and it’ll help you construct top-notch UIs with minimal coding.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">Check out&nbsp;<a href=\"http://dhtmlx.com/touch/samples/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">samples</a>&nbsp;of DHTMLX Touch in action, which includes a&nbsp;<a href=\"http://dhtmlx.com/touch/samples/apps/menu/index.html\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">menu app for the iPad</a>(perfect for restaurants) and Book Shop (a proof-of-concept demo for an e-store offering books).</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">9.&nbsp;<a href=\"http://mobilizejs.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Mobilize.js</a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://mobilizejs.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-10_mobilize_js.png\" width=\"550\" height=\"265\" alt=\"Mobilize.js\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">Mobilize.js is a JavaScript framework for porting your existing website to a mobile-friendly version. It’s easy to integrate into your site because it does everything on the client-side, and has facilities for detecting mobile browsers so that you can serve them the mobile version of the site.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://sixrevisions.com/category/wordpress/\" title=\"WordPress category on Six Revisions\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">WordPress</a>&nbsp;users might want to check out the&nbsp;<a href=\"http://cdn.mobilizejs.com/docs/trunk/manual/integrations/wordpress.html#installing-mobilize-js-for-wordpress-plug-in\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">Mobilize.js WordPress plugin</a>.</p><h3 style=\"margin-left: 50px; padding-top: 24px; padding-bottom: 5px; font-size: 17px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">10.&nbsp;<a href=\"http://chocolatechipmobile.wordpress.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><span id=\"sceditor-start-marker\" style=\"line-height: 0; display: none;\" class=\"sceditor-selection sceditor-ignore\"></span>ChocolateChip Mobile<span id=\"sceditor-end-marker\" style=\"line-height: 0; display: none;\" class=\"sceditor-selection sceditor-ignore\"></span></a></h3><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\"><a href=\"http://chocolatechipmobile.wordpress.com/\" style=\"color: rgb(0, 102, 153); text-decoration: none;\"><img src=\"http://cdn.sixrevisions.com/0180-11_chocolatechip_mobile.jpg\" width=\"550\" height=\"265\" alt=\"ChocolateChip Mobile\" style=\"border: 1px solid rgb(204, 204, 204); padding: 4px;\"></a></p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">ChocolateChip Mobile is a JavaScript framework developed towards future-forward web browsers. It shares syntax similar to jQuery or Prototype.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">The resemblance to jQuery doesn’t stop at the syntax — for example, like jQuery, it can handle events through binding and delegation, and has methods such as&nbsp;<code style=\"line-height: normal; font-family: \'Courier New\', Courier, monospace;\">.css</code>&nbsp;and<code style=\"line-height: normal; font-family: \'Courier New\', Courier, monospace;\">.toggleClass</code>.</p><p style=\"margin-bottom: 15px; margin-left: 50px; line-height: 20px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">You can grab ChocolateChip Mobile’s&nbsp;<a href=\"https://github.com/rbiggs/chocolatechip\" style=\"color: rgb(0, 102, 153); text-decoration: none;\">source code off GitHub</a>.</p></p>', 'quote\\', '', 'http://sixrevisions.com/javascript/mobile%C2%A0web-development-frameworks/', '', '100', 'en_SG', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2013-06-01 16:41:11', '2013-06-02 00:41:11', '0');
INSERT INTO `cm_article` VALUES ('8a3321b63ef3031f013f03fc39980117', '', 'Mobile UI', 'Collapsibles, Controlgroups, Listviews, Navigation, Panels, Popups, Responsive Tables, Sliders, Swipe', '<p><font face=\"Times New Roman\" size=\"3\"><b>Form Element</b></font></p><p><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Input(Text, password, search, url, email, number, tel, date, time, month, datetime),</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">&nbsp;Checks, Radios,&nbsp;</span><font face=\"Times New Roman\" size=\"3\">Select element, DropDownList, Slider, Switch,&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"4\"><b>Buttons&amp;Tabs</b></font></p><p><font face=\"Times New Roman\" size=\"3\">Badges, ButtonGroup, NavBar, TabStrip, &nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><b style=\"font-family: \'Times New Roman\'; font-size: medium;\">ListView</b><br></p><p><font face=\"Times New Roman\" size=\"3\">Flat, Grouped, Detail Buttons, Pull to fresh, Endless Scrolling, Press to load more, Fixed Headers, Editing, Filtering,</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"3\"><b>Scroller</b></font></p><p><font face=\"Times New Roman\" size=\"3\">Pull to refresh, ScrollView,&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"4\"><b>Popups</b></font></p><p><font face=\"Times New Roman\" size=\"3\">ActionSheet, PopOver, Loading popup</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><b style=\"font-family: \'Times New Roman\'; font-size: medium;\">View Transitions</b><br></p><p><font face=\"Times New Roman\" size=\"3\">Shade, Slide, Flip,&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"3\"><b>Layout</b>&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\">SplitView,&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"3\"><b>Graphic(DataViz)&nbsp;</b></font></p><p><font face=\"Times New Roman\" size=\"3\">Area&nbsp;</font><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Chart</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">, Bar&nbsp;</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Chart</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">, Bubble&nbsp;</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">Chart</span><span style=\"font-family: \'Times New Roman\'; font-size: medium;\">, Bullet Chart, Line Chart, Donut Chart, Pie Chart, Scatter Chart, Sparklines Chart, Stock Chart, Radial Gauge, Linear Gauge,&nbsp;</span></p><div><font face=\"Times New Roman\" size=\"3\"><br></font></div><div><font face=\"Times New Roman\" size=\"3\">Gauges</font></div><p><font face=\"Times New Roman\" size=\"3\"><b><span id=\"sceditor-start-marker\" class=\"sceditor-selection sceditor-ignore\" style=\"line-height: 0; display: none;\"></span>Touch Events<span id=\"sceditor-end-marker\" class=\"sceditor-selection sceditor-ignore\" style=\"line-height: 0; display: none;\"></span></b></font></p><p><font face=\"Times New Roman\" size=\"3\">Touch, swipe, double-tap, tap, hold,&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"3\"><b>Other</b></font></p><p><font face=\"Times New Roman\" size=\"3\">ModalView,&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\"><br></font></p><p><font face=\"Times New Roman\" size=\"3\"><b>Related Resource</b>&nbsp;</font></p><p><font face=\"Times New Roman\" size=\"3\">Kendo UI</font></p>', 'Gan Jianping', '', '', '', '100', 'en_SG', '', '4028d98139636d9d013963783d60007c', 'Gan Jianping', '2013-06-02 08:23:48', '2013-06-02 17:33:54', '0');
