INSERT INTO cm_article VALUES ('4028d98139b4c9e40139b54afa6c03f0', 'spring01', 'SPRING FRAMEWORK OVERVIEW', '<p><span style=\"font-size: medium;\">The Spring Framework provides a comprehensive programming and configuration model for modern Java-based enterprise applications - on any kind of deployment platform. A key element of Spring is infrastructural support at the application level: Spring focuses on the \"plumbing\" of enterprise applications so that teams can focus on application-level business logic, without unnecessary ties to specific deployment environments.</span></p>\r\n<p><span style=\"font-size: medium;\">Spring includes:</span></p>\r\n<ul>\r\n<li><span style=\"font-size: medium;\">Flexible dependency injection with XML and annotation-based configuration styles</span></li>\r\n<li><span style=\"font-size: medium;\">Advanced support for aspect-oriented programming with proxy-based and AspectJ-based variants</span></li>\r\n<li><span style=\"font-size: medium;\">Support for declarative transactions, declarative caching, declarative validation, and declarative formatting</span></li>\r\n<li><span style=\"font-size: medium;\">Powerful abstractions for working with common Java EE specifications such as JDBC, JPA, JTA and JMS</span></li>\r\n<li><span style=\"font-size: medium;\">First-class support for common open source frameworks such as Hibernate and Quartz</span></li>\r\n<li><span style=\"font-size: medium;\">A flexible web framework for building RESTful MVC applications and service endpoints</span></li>\r\n<li><span style=\"font-size: medium;\">Rich testing facilities for unit tests as well as for integration tests</span></li>\r\n</ul>\r\n<p><span style=\"font-size: medium;\">Spring is modular in design, allowing for incremental adoption of individual parts such as the core container or the JDBC support. While all Spring services are a perfect fit for the Spring core container, many services can also be used in a programmatic fashion outside of the container.</span></p>\r\n<p><span style=\"font-size: medium;\">Supported deployment platforms range from standalone applications to Tomcat and Java EE servers such as WebSphere. Spring is also a first-class citizen on major cloud platforms with Java support, e.g. on Heroku, Google App Engine, Amazon Elastic Beanstalk and VMware\'s Cloud Foundry.</span></p>\r\n<p>&nbsp;</p>', 'quote', '', 'http://www.springsource.org/spring-framework', 'spring', '1', 'en_SG', '', 'admin', 'admin', '2012-09-11 20:25:53', '2012-09-11 21:39:15', '0');
INSERT INTO cm_article VALUES ('4028d98139bb682e0139bbb05b4703d5', '', 'Working together', '<div class=\"conver_list\" style=\"margin: 10px 0px 0px; padding: 0px 0px 4px; width: 685px;\">\r\n<table class=\"txt\" style=\"margin: 0px; padding: 0px; border: 0px; width: 685px; word-break: break-all; table-layout: fixed; word-wrap: break-word;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody style=\"margin: 0px; padding: 0px;\">\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td class=\"ab\" style=\"margin: 0px; padding: 0px 15px 0px 4px; font-size: 15px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" align=\"right\" width=\"125\"><strong style=\"margin: 0px; padding: 0px;\">Jim</strong></td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"con u\" style=\"margin: 0px; padding: 0px 0px 5px; text-decoration: underline; float: left; font-size: 15px;\">老板居然让我和迈克共事，难以置信啊！</p>\r\n</td>\r\n</tr>\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" width=\"125\">&nbsp;</td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"tc_sub\" style=\"margin: 0px; padding: 0px; color: #a0a0a0; float: left;\">I can\'t believe our boss is making me work together with Mike.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<div class=\"conver_list\" style=\"margin: 10px 0px 0px; padding: 0px 0px 4px; width: 685px;\">\r\n<table class=\"txt\" style=\"margin: 0px; padding: 0px; border: 0px; width: 685px; word-break: break-all; table-layout: fixed; word-wrap: break-word;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody style=\"margin: 0px; padding: 0px;\">\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" width=\"125\">&nbsp;</td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"tc_sub f_pron\" style=\"margin: 0px; padding: 0px 0px 3px; color: #a0a0a0; font-family: \'lucida sans unicode\', sans-serif; word-break: break-all !important; float: left;\">Zěnme le? tā fēich&aacute;ng yǒu jīngy&agrave;n de.</p>\r\n</td>\r\n</tr>\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td class=\"ab\" style=\"margin: 0px; padding: 0px 15px 0px 4px; font-size: 15px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" align=\"right\" width=\"125\"><strong style=\"margin: 0px; padding: 0px;\">Jack</strong></td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"con u\" style=\"margin: 0px; padding: 0px 0px 5px; text-decoration: underline; float: left; font-size: 15px;\">怎么了？他非常有经验的。</p>\r\n</td>\r\n</tr>\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" width=\"125\">&nbsp;</td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"tc_sub\" style=\"margin: 0px; padding: 0px; color: #a0a0a0; float: left;\">Why not? He\'s very experienced.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<div class=\"conver_list\" style=\"margin: 10px 0px 0px; padding: 0px 0px 4px; width: 685px;\">\r\n<table class=\"txt\" style=\"margin: 0px; padding: 0px; border: 0px; width: 685px; word-break: break-all; table-layout: fixed; word-wrap: break-word;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody style=\"margin: 0px; padding: 0px;\">\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" width=\"125\">&nbsp;</td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"tc_sub f_pron\" style=\"margin: 0px; padding: 0px 0px 3px; color: #a0a0a0; font-family: \'lucida sans unicode\', sans-serif; word-break: break-all !important; float: left;\">Kě tā k&agrave;n qǐl&aacute;i hěn shǎ.</p>\r\n</td>\r\n</tr>\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td class=\"ab\" style=\"margin: 0px; padding: 0px 15px 0px 4px; font-size: 15px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" align=\"right\" width=\"125\"><strong style=\"margin: 0px; padding: 0px;\">Jim</strong></td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"con u\" style=\"margin: 0px; padding: 0px 0px 5px; text-decoration: underline; float: left; font-size: 15px;\">可他看起来很傻。</p>\r\n</td>\r\n</tr>\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\" width=\"125\">&nbsp;</td>\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-family: Arial, simsun; overflow: hidden;\">\r\n<p class=\"tc_sub\" style=\"margin: 0px; padding: 0px; color: #a0a0a0; float: left;\">But he looks like a jerk.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<div class=\"conver_list\" style=\"margin: 10px 0px 0px; padding: 0px 0px 4px; width: 685px;\">\r\n<table class=\"txt\" style=\"margin: 0px; padding: 0px; border: 0px; width: 685px; word-break: break-all; table-layout: fixed; word-wrap: break-word;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody style=\"margin: 0px; padding: 0px;\">\r\n<tr style=\"margin: 0px; padding: 0px;\">\r\n<td style=\"margin: 0px; padding: 0px; font-size: 12px; color: #333333; font-fam